import React from "react";

class ArrowFunc extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: ""
        }
    }

    componentDidMount() {
        fetch("https://picsum.photos/v2/list?page=2&limit=100")
            .then(response => response.json())  // Use response.json() to parse JSON data
            .then(data => {
                this.setState({ data: JSON.stringify(data) });
                console.log(data)
                // Convert data to a string for displaying in input element
            })
            .catch(error => console.log("Error Fetching Data : ", error));
    }

    render() {
        return (
            <div>
                <h2>React Constructor Example</h2>
                <p> value={this.state.data} readOnly </p>
            </div>
        )
    }
}

export default ArrowFunc;
