import React from "react"
export default class ControlledExmple extends React.Component{
constructor(props){
    super(props)
    this.state={
        controlled:"Controlled Component",
        descrption:"A controlled component is bound to a value, and its changes will be handled in code by using event-based callbacks. Here, the input form element is handled by the react itself rather than the DOM. In this, the mutable state is kept in the state property and will be updated only with setState() method.Controlled components have functions that govern the data passing into them on every onChange event occurs. This data is then saved to state and updated with setState() method. It makes component have better control over the form elements and data.",
    }
}
render(){
    return (
        <div>
            <h1>{this.state.controlled}</h1>
        </div>
    )
}
}
