export default class FindDOMNode extends React.Component {
    constructor(){
        super();
        this.findDOMNodeExporter1 = this.findDOMNodeExporter1.bind(this);
        this.findDOMNodeExporter2 = this.findDOMNodeExporter2.bind(this);
    }

    findDOMNodeExporter1(){
        var firstDiv = document.getElementById('DivOne');
        ReactDOM.findDOMNode(firstDiv).style.color = "yellow";
    }

    findDOMNodeExporter2(){
        var secondDiv = document.getElementById('DivTwo');
        ReactDOM.findDOMNode(secondDiv).style.color = "brown";
    }

    render(){
        return (
            <div>
                <h1>Example by Myself</h1>
                <button onClick={this.findDOMNodeExporter1}>Change Color of DivOne</button>
                <button onClick={this.findDOMNodeExporter2}>Change Color of DivTwo</button>
                <h3 id="DivOne">Hello from 1</h3>
                <h3 id="DivTwo">Hello from 2</h3>
            </div>
        );
    }
}
