import React from "react";

class DefaultProps extends React.Component {
    render() {
        return (
            <div>
                <h1>This is Default Props</h1>
                <h3>Welcome to {this.props.name}</h3>
                <p>It is not necessary to always add props in the reactDom.render() element. You can also set default props directly on the component constructor. It can be explained in the below example.</p>
            </div>
        )
    }
}

DefaultProps.defaultProps = {
    name: 'Default Props'
};

export default DefaultProps;
