import React from "react";

class ArrowFunc extends React.Component{
    constructor(props){
        super(props)
        this.state={
            data:""

        }

    }
    componentDivemount(){
        fetch("www.javapoint.com")
        .then(response =>response.text())
        .then(data=>{
            this.setState({data})
        })
        .catch(error =>console.log("Error Fetching Data : ",error))
    }
    handelEvent=()=>{
        console.log(this.data)

    }
    render(){
        return(
            <div>
                <h2>React Constructor Example</h2>
                <input type="text"value={this.state.data} rows={10} cols={50} readOnly/>
                {/* <button onClick={this.handelEvent}>Please click</button> */}
            </div>
        )
    }
}
export default ArrowFunc
