export default class setStateExample extends React.Component{
    constructor(){
        super()
        this.state={
            name:'Wasim',
            age: 23,
            exp:"3 Years"
        }
        this.UpdateSetState=this.UpdateSetState.bind(this)
    }
    UpdateSetState(){
        this.state={
            name:"John Doe",
            age: 27,
            exp:"8 Years"
        }
    }
    render(){
        return(
            <section>
                <h2>{this.state.name}</h2>
                <h2>{this.state.age}</h2>
                <h2>{this.state.exp}</h2>
                <button onClick={this.UpdateSetState}>Click</button>
            </section>
        )
    }
}
