export default class findDOMNode extends React.Component{
    constructor(){
        super()
        this.findDOMNodeExporter1 = this.findDOMNodeExporter1.bind(this)
        this.findDOMNodeExporter2 = this.findDOMNodeExporter2.bind(this)
    }
    findDOMNodeExporter1(){
        var fistDiv = document.getElementById('DivOne')
        ReactDOM.findDOMNode(fistDiv).computedStyleMap.color = "yellow"
    }
    findDOMNodeExporter2(){
        var fistDiv = document.getElementById('DivTwo')
        ReactDOM.findDOMNode(fistDiv).computedStyleMap.color = "brown"
    }
    render(){
        return (
            <div>
                <h1>Example by My self</h1>
                <button onClick={this.findDOMNodeExporter1}>Fist DOM</button>
                <button onClick={this.findDOMNodeExporter2}>Fist DOM</button>
