    forceUpdateData(){
        this.forceUpdate()
    }
    randomColor=()=>{
        let letters ='0123456789'
        let color='#'
        for( let i=0; i<6;i++){
            color+letters[Math.floor(Math.random()*16)]
        }
    }
    render(){
        return(
            <div>
                <h1>Example of Force Update</h1>
                <button onClick={this.forceUpdateData}>Force Update</button>
                <h1 style={this.newStyle}>{this.forceUpdateData}</h1>
