import React from "react";

class StateAndProps extends React.Component{
    constructor(props){
        super(props)
        this.state={
            name: 'John Doe',
            age: 23
        }
    }
    render(){
        return <div>
        <ABC abcProp ={this.state.name} age={this.state.age}/>

        </div>
    }

}

class ABC extends React.Component{
    render(){
        return (
            <div>
                <h1>State And props Example</h1>
                <h3>Welocome to {this.props.abcProp}</h3>
                <h3>Age{this.props.age}</h3>
            </div>
        )
    }
}
export default StateAndProps
