import React from "react"
class StateAndProps extends React.Component{
    constructor(props){
        super(props)
        this.state={
            name:'Props Example'
        }
    }
    render(){
        return (
            <div>
                <ABC abcProp = {this.state.name}/>
            </div>
        )
    }

}
class ABC extends React.Component{
    render(){
        return(
            <div>
               <h1>State and Props Example</h1>
               <h3>Welcome to {this.props.abcProp}</h3>
            </div>
        )
    }
}
export default StateAndProps
