import React from "react";

export default class FindDomNodeExample extends React.Component{
    constructor(){
        super()
        this.findDomNodeHandler1 = this.findDomNodeHandler1.bind(this);
        this.findDomNodeHandler2 = this.findDomNodeHandler2.bind(this);

    };
    findDomNodeHandler1(){
        var myDiv = document.getElementById('mydivOne')
        ReactDOM.findDOMNode(myDivOne).style.color ="red"
    }
    findDomNodeHandler2(){
        var myDiv = document.getElementById('mydivTwo')
        ReactDOM.findDOMNode(myDivTwo).style.color ="blue"
    }
    render(){
        return (
            <div>
                <h1>ReactJS Find DOM Node Examplr</h1>
                <button onClick={this.findDomNodeHandler1}>FIND_DOM_NODE_1</button>
                <button onClick={this.findDomNodeHandler2}>FIND_DOM_NODE_2</button>
            </div>

        )
    }
}
