import React from "react";

class ControlledExample extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      controlled: "Controlled Component",
      description:
        "A controlled component is bound to a value, and its changes will be handled in code by using event-based callbacks. Here, the input form element is handled by the react itself rather than the DOM. In this, the mutable state is kept in the state property and will be updated only with setState() method. Controlled components have functions that govern the data passing into them on every onChange event occurs. This data is then saved to state and updated with setState() method. It makes component have better control over the form elements and data.",
      showUncontrolled: false
    };
    this.toggleDisplay = this.toggleDisplay.bind(this);
  }

  toggleDisplay() {
    this.setState({ showUncontrolled: true });
  }

  render() {
    return (
      <div>
        <h1>{this.state.controlled}</h1>
        <h3>{this.state.description}</h3>
        <TableInfo />
        <button onClick={this.toggleDisplay}>Toggle to Uncontrolled</button>

        {this.state.showUncontrolled && <TableofInfoUncontrolled />}
      </div>
    );
  }
}

class TableInfo extends React.Component {
  render() {
    return (
      <section>
        <h1>Controlled</h1>
        <div>
          <ol>
            <li>
              <h4>It does not maintain its internal state.</h4>
            </li>
            <li>
              <h4>Here, data is controlled by the parent component.</h4>
            </li>
            <li>
              <h4>It accepts its current value as a prop.</h4>
            </li>
            <li>
              <h4>It allows validation control.</h4>
            </li>
            <li>
              <h4>It has better control over the form elements and data.</h4>
            </li>
          </ol>
        </div>
      </section>
    );
  }
}

class TableofInfoUncontrolled extends React.Component {
  render() {
    return (
      <section>
        <h1>Uncontrolled</h1>
        <h4>It maintains its internal states.</h4>
        <h4>Here, data is controlled by the DOM itself.</h4>
        <h4>It uses a ref for their current values.</h4>
        <h4>It does not allow validation control.</h4>
        <h4>It has limited control over the form elements and data.</h4>
      </section>
    );
  }
}

export default ControlledExample;
